// Convenience helpers (optional)
function losungenFindByDateText(q) {
  if (!q) return null;
  const needle = String(q).toLowerCase();
  return LOSUNGEN_DATA.find(e => (e.date_text || '').toLowerCase().includes(needle)) || null;
}

function losungenFindByUrl(url) {
  return LOSUNGEN_DATA.find(e => e.url === url) || null;
}

// Expose globally for non-module usage
window.LOSUNGEN_DATA = LOSUNGEN_DATA;
window.losungenFindByDateText = losungenFindByDateText;
window.losungenFindByUrl = losungenFindByUrl;
